public class Main{	public static void main(String[] args) {        int i, a, b, c, a3, b3, c3;               for (i = 100; i < 1000; i = i + 1) {            a = i / 100;            b = i / 10 % 10;            c = i % 10;                        a3 = a * a * a;            b3 = b * b * b;            c3 = c * c * c;                        if (i == (a3 + b3 + c3))                System.out.println(i);        }            	}}